changeSet.sql:
-- Create a default public group named default_public_group
INSERT INTO collect.ofc_usergroup(id, name, label, created_by, creation_date, visibility_code) VALUES ( nextval('collect.ofc_usergroup_id_seq'), 'default_public_group', 'Default Public Group', (SELECT u.id FROM collect.ofc_user u WHERE u.username = 'admin'), CURRENT_TIMESTAMP, 'P' );
-- make the admin user owner of the Default Public group
INSERT INTO collect.ofc_user_usergroup (user_id, group_id, role_code, status_code, request_date, member_since) VALUES ( (SELECT u.id FROM collect.ofc_user u WHERE u.username = 'admin'), (SELECT ug.id FROM collect.ofc_usergroup ug WHERE ug.name = 'default_public_group'), 'O', 'A', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP );
-- Create a user group named USERNAME_default_private_group per each user
INSERT INTO collect.ofc_usergroup(id, name, label, created_by, creation_date, visibility_code) SELECT nextval('collect.ofc_usergroup_id_seq'), CONCAT(u.username, '_default_private_group'), CONCAT('Private group of ', u.username), u.id, CURRENT_TIMESTAMP, 'N' FROM collect.ofc_user u ;
-- Make each user owner of his default group
INSERT INTO collect.ofc_user_usergroup (user_id, group_id, role_code, status_code, request_date, member_since) SELECT u.id, (SELECT ug.id FROM collect.ofc_usergroup ug WHERE ug.name = CONCAT(u.username, '_default_private_group')), 'O', 'A', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP FROM collect.ofc_user u ;
-- Make each user with administrator role be even administrator of the default_public_group (backwards compatibility)
INSERT INTO collect.ofc_user_usergroup (user_id, group_id, role_code, status_code, request_date, member_since) SELECT u.id, (SELECT ug.id FROM collect.ofc_usergroup ug WHERE ug.name = 'default_public_group'), 'A', 'A', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP FROM collect.ofc_user u, collect.ofc_user_role ur WHERE ur.user_id = u.id AND u.username != 'admin' AND ur.role = 'ROLE_ADMIN' ;
-- Users with role ROLE_ANALYSIS will be Analyzers in default_public_group
INSERT INTO collect.ofc_user_usergroup (user_id, group_id, role_code, status_code, request_date, member_since) SELECT u.id, (SELECT ug.id FROM collect.ofc_usergroup ug WHERE ug.name = 'default_public_group'), 'D', 'A', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP FROM collect.ofc_user u, collect.ofc_user_role ur WHERE u.id NOT IN ( SELECT uug.user_id FROM collect.ofc_user_usergroup uug WHERE uug.group_id = ( SELECT ug.id FROM collect.ofc_usergroup ug WHERE ug.name = 'default_public_group') ) AND ur.user_id = u.id AND u.username != 'admin' AND ur.role = 'ROLE_ANALYSIS' ;
-- Users with role ROLE_CLEANSING will be only Analyzers in default_public_group
INSERT INTO collect.ofc_user_usergroup (user_id, group_id, role_code, status_code, request_date, member_since) SELECT u.id, (SELECT ug.id FROM collect.ofc_usergroup ug WHERE ug.name = 'default_public_group'), 'D', 'A', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP FROM collect.ofc_user u, collect.ofc_user_role ur WHERE u.id NOT IN ( SELECT uug.user_id FROM collect.ofc_user_usergroup uug WHERE uug.group_id = ( SELECT ug.id FROM collect.ofc_usergroup ug WHERE ug.name = 'default_public_group') ) AND ur.user_id = u.id AND u.username != 'admin' AND ur.role = 'ROLE_CLEANSING' ;
-- Users with role ROLE_ENTRY will be only (data entry) Operators in default_public_group
INSERT INTO collect.ofc_user_usergroup (user_id, group_id, role_code, status_code, request_date, member_since) SELECT u.id, (SELECT ug.id FROM collect.ofc_usergroup ug WHERE ug.name = 'default_public_group'), 'U', 'A', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP FROM collect.ofc_user u, collect.ofc_user_role ur WHERE u.id NOT IN ( SELECT uug.user_id FROM collect.ofc_user_usergroup uug WHERE uug.group_id = ( SELECT ug.id FROM collect.ofc_usergroup ug WHERE ug.name = 'default_public_group') ) AND ur.user_id = u.id AND u.username != 'admin' AND ur.role = 'ROLE_ENTRY' ;
-- Users with role ROLE_VIEW will be only Viewers in default_public_group
INSERT INTO collect.ofc_user_usergroup (user_id, group_id, role_code, status_code, request_date, member_since) SELECT u.id, (SELECT ug.id FROM collect.ofc_usergroup ug WHERE ug.name = 'default_public_group'), 'V', 'A', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP FROM collect.ofc_user u, collect.ofc_user_role ur WHERE u.id NOT IN ( SELECT uug.user_id FROM collect.ofc_user_usergroup uug WHERE uug.group_id = ( SELECT ug.id FROM collect.ofc_usergroup ug WHERE ug.name = 'default_public_group') ) AND ur.user_id = u.id AND u.username != 'admin' AND ur.role = 'ROLE_VIEW' ;
-- Assign all existing survey to the Default group
UPDATE collect.ofc_survey SET group_id = (SELECT ug.id FROM collect.ofc_usergroup ug WHERE ug.name = 'default_public_group');
