changeSet.sql:
-- Create a default public group named default_public_group
INSERT INTO ofc_usergroup(id, name, label, created_by, creation_date, visibility_code) VALUES ( (SELECT MAX(ug.id) + 1 FROM ofc_usergroup ug), 'default_public_group', 'Default Public Group', (SELECT u.id FROM ofc_user u WHERE u.username = 'admin'), CURRENT_TIMESTAMP, 'P' );
-- make the admin user owner of the Default Public group
INSERT INTO ofc_user_usergroup (user_id, group_id, role_code, status_code, request_date, member_since) VALUES ( (SELECT u.id FROM ofc_user u WHERE u.username = 'admin'), (SELECT ug.id FROM ofc_usergroup ug WHERE ug.name = 'default_public_group'), 'O', 'A', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP );
-- Create a user group named USERNAME_default_private_group per each user
INSERT INTO ofc_usergroup(id, name, label, created_by, creation_date, visibility_code) SELECT (SELECT MAX(ug.id) + 1 FROM ofc_usergroup ug), u.username || '_default_private_group', 'Private group of ' || u.username, u.id, CURRENT_TIMESTAMP, 'N' FROM ofc_user u ;
-- Make each user owner of his default group
INSERT INTO ofc_user_usergroup (user_id, group_id, role_code, status_code, request_date, member_since) SELECT u.id, (SELECT ug.id FROM ofc_usergroup ug WHERE ug.name = u.username || '_default_private_group'), 'O', 'A', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP FROM ofc_user u ;
-- Make each user with administrator role be even administrator of the default_public_group (backwards compatibility)
INSERT INTO ofc_user_usergroup (user_id, group_id, role_code, status_code, request_date, member_since) SELECT u.id, (SELECT ug.id FROM ofc_usergroup ug WHERE ug.name = 'default_public_group'), 'A', 'A', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP FROM ofc_user u, ofc_user_role ur WHERE ur.user_id = u.id AND u.username != 'admin' AND ur.role = 'ROLE_ADMIN' ;
-- Users with role ROLE_ANALYSIS will be Analyzers in default_public_group
INSERT INTO ofc_user_usergroup (user_id, group_id, role_code, status_code, request_date, member_since) SELECT u.id, (SELECT ug.id FROM ofc_usergroup ug WHERE ug.name = 'default_public_group'), 'D', 'A', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP FROM ofc_user u, ofc_user_role ur WHERE u.id NOT IN ( SELECT uug.user_id FROM ofc_user_usergroup uug WHERE uug.group_id = ( SELECT ug.id FROM ofc_usergroup ug WHERE ug.name = 'default_public_group') ) AND ur.user_id = u.id AND u.username != 'admin' AND ur.role = 'ROLE_ANALYSIS' ;
-- Users with role ROLE_CLEANSING will be only Analyzers in default_public_group
INSERT INTO ofc_user_usergroup (user_id, group_id, role_code, status_code, request_date, member_since) SELECT u.id, (SELECT ug.id FROM ofc_usergroup ug WHERE ug.name = 'default_public_group'), 'D', 'A', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP FROM ofc_user u, ofc_user_role ur WHERE u.id NOT IN ( SELECT uug.user_id FROM ofc_user_usergroup uug WHERE uug.group_id = ( SELECT ug.id FROM ofc_usergroup ug WHERE ug.name = 'default_public_group') ) AND ur.user_id = u.id AND u.username != 'admin' AND ur.role = 'ROLE_CLEANSING' ;
-- Users with role ROLE_ENTRY will be only (data entry) Operators in default_public_group
INSERT INTO ofc_user_usergroup (user_id, group_id, role_code, status_code, request_date, member_since) SELECT u.id, (SELECT ug.id FROM ofc_usergroup ug WHERE ug.name = 'default_public_group'), 'U', 'A', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP FROM ofc_user u, ofc_user_role ur WHERE u.id NOT IN ( SELECT uug.user_id FROM ofc_user_usergroup uug WHERE uug.group_id = ( SELECT ug.id FROM ofc_usergroup ug WHERE ug.name = 'default_public_group') ) AND ur.user_id = u.id AND u.username != 'admin' AND ur.role = 'ROLE_ENTRY' ;
-- Users with role ROLE_VIEW will be only Viewers in default_public_group
INSERT INTO ofc_user_usergroup (user_id, group_id, role_code, status_code, request_date, member_since) SELECT u.id, (SELECT ug.id FROM ofc_usergroup ug WHERE ug.name = 'default_public_group'), 'V', 'A', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP FROM ofc_user u, ofc_user_role ur WHERE u.id NOT IN ( SELECT uug.user_id FROM ofc_user_usergroup uug WHERE uug.group_id = ( SELECT ug.id FROM ofc_usergroup ug WHERE ug.name = 'default_public_group') ) AND ur.user_id = u.id AND u.username != 'admin' AND ur.role = 'ROLE_VIEW' ;
-- Assign all existing survey to the Default group
UPDATE ofc_survey SET group_id = (SELECT ug.id FROM ofc_usergroup ug WHERE ug.name = 'default_public_group');
